public function exportTableAction()\n{\n    // Disable Layout and View Renderer\n    $this->_helper->layout->disableLayout();\n    $this->_helper->viewRenderer->setNoRender();\n\n    // Get parameters\n    $fileName = $this->_getParam('fileName') ?? 'export.csv';\n    $tableName = $this->_getParam('tableName');\n\n    // Disable Action Stack Plugins\n    $frontController = Zend_Controller_Front::getInstance();\n    $actionStack = $frontController->getPlugin('Zend_Controller_Plugin_ActionStack');\n    if ($actionStack) {\n        $actionStack->setEnabled(false);\n    }\n\n    // Set headers for file download\n    header('Content-Type: application/octet-stream');\n    header('Content-Disposition: attachment; filename="' . basename($fileName) . '"');\n\n    // Output CSV data\n    echo $this->getCsv($tableName, $fileName);\n\n    // Exit to ensure no other output is sent\n    exit();\n}\n\nprotected function getCsv($tableName, $fileName)\n{\n    // Implement logic to fetch data from the database and format it as CSV\n    // Example:\n    $data = $this->_getTableGateway($tableName)->fetchAll()->toArray();\n\n    $output = fopen('php://output', 'w');\n    fputcsv($output, array_keys(reset($data)));\n\n    foreach ($data as $row) {\n        fputcsv($output, $row);\n    }\n\n    fclose($output);\n\n    return ob_get_clean();\n}