$url = 'http://www.arstechnica.com';\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 20); // Connect timeout in seconds\ncurl_setopt($ch, CURLOPT_TIMEOUT, 30);        // Overall timeout in seconds\ncurl_setopt($ch, CURLOPT_USERAGENT, $_SERVER['HTTP_USER_AGENT']);\ncurl_setopt($ch, CURLOPT_HEADER, true);\ncurl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'HEAD');\n\n// Optional: Verbose output to help diagnose issues\ncurl_setopt($ch, CURLOPT_VERBOSE, true);\n\n$content = curl_exec ($ch);\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n}\n\ninfo = curl_getinfo($ch); // Get information about the request\nprint_r($info);\ncurl_close($ch);