function getLeafNodes($nodes) {\n    $leafNodes = [];\n    \n    foreach ($nodes as $node) {\n        if (empty($node['Child'])) {\n            // It's a leaf node\n            $categoryId = $node['Category']['id'];\n            $categoryName = $node['Category']['name'];\n            $leafNodes[$categoryId] = $categoryName;\n        } else {\n            // Recursively find leaf nodes in children\n            $leafNodes = array_merge($leafNodes, getLeafNodes($node['Child']));\n        }\n    }\n    \n    return $leafNodes;\n}\n\n// Example usage:\n$nodes = [\n    0 => [\n        'Category' => ['id' => 49, 'name' => 'Poster'],\n        'Child' => []\n    ],\n    1 => [\n        'Category' => ['id' => 48, 'name' => 'Sticker'],\n        'Child' => []\n    ],\n    2 => [\n        'Category' => ['id' => 46, 'name' => 'Hat'],\n        'Child' => []\n    ],\n    3 => [\n        'Category' => ['id' => 45, 'name' => 'Hoodie'],\n        'Child' => []\n    ],\n    7 => [\n        'Category' => ['id' => 36, 'name' => 'Merch'],\n        'Child' => [\n            ['id' => 49, 'name' => 'Poster'], \n            ['id' => 48, 'name' => 'Sticker'], \n            ['id' => 40, 'name' => 'Apparel']\n        ]\n    ]\n];\n\n$leafNodes = getLeafNodes($nodes);\nprint_r($leafNodes);