<?php\nsession_start();\n\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    // Validate inputs here\n    $errors = [];\n    \n    if (empty($_POST['items'])) {\n        $errors[] = 'Items field is required.';\n    }\n    // More validations...\n\n    if (count($errors) === 0) {\n        // Save data to session\n        $_SESSION['form_data'] = $_POST;\n        \n        // Redirect to check.php using PRG pattern\n        header('Location: check.php');\n        exit();\n    } else {\n        // Errors found, display them\n    }\n}\n\n// Optionally load form data from session for editing if needed\n$formData = isset($_SESSION['form_data']) ? $_SESSION['form_data'] : [];\n$_SESSION['form_data'] = null; // Clear the data after displaying it once\n?>\n\n<form method="post" action="<?php echo htmlspecialchars($_SERVER['PHP_SELF']); ?>">\n    <input type="text" name="items" value="<?php echo htmlspecialchars($formData['items'] ?? ''); ?>">\n    <!-- More fields... -->\n    <button type="submit">Submit</button>\n</form>\n\n<?php\nif (!empty($errors)) {\n    foreach ($errors as $error) {\n        echo "<p style='color:red;'>$error</p>";\n    }\n}\n?>