class myParent {\n    static function hello() {\n        echo self::getMyVar();  \n    }\n    \n    protected static function getMyVar() {\n        return 'world';\n    }\n}\n\nclass myFirstChild extends myParent {\n    protected static function getMyVar() {\n        return 'earth';\n    }\n}\n\nclass mySecondChild extends myParent {\n    protected static function getMyVar() {\n        return 'planet';\n    }\n}\n\nmyFirstChild::hello(); // Outputs: earth\nmySecondChild::hello(); // Outputs: planet