<?php\nheader('Content-Type: application/json');\n\n// Database connection settings\n$host = 'localhost';\n$dbname = 'your_database_name';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    // Create PDO instance\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Get the last fetched timestamp from GET request\n    if (isset($_GET['last_fetch'])) {\n        $lastFetchTime = $_GET['last_fetch'];\n    } else {\n        $lastFetchTime = '1970-01-01 00:00:00'; // Default to Unix epoch time\n    }\n\n    // Query the database for messages newer than last fetch time\n    $stmt = $pdo->prepare("SELECT author, text, date_written FROM messages WHERE date_written > :last_fetch ORDER BY date_written ASC");\n    $stmt->bindParam(':last_fetch', $lastFetchTime);\n    $stmt->execute();\n    $messages = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Return JSON response\n    echo json_encode($messages);\n} catch (PDOException $e) {\n    http_response_code(500);\n    echo json_encode(['error' => 'Database error: ' . $e->getMessage()]);\n}\n?>