function array_key_exists_deep($needle, $haystack) {\n    $keys = explode('=>', $needle);\n    foreach ($keys as $key) {\n        $key = trim($key);\n        if (is_array($haystack) && array_key_exists($key, $haystack)) {\n            $haystack = $haystack[$key];\n        } else {\n            return false;\n        }\n    }\n    return true;\n}\n\n// Usage\n$path = 'f=>b=>g';\nif (array_key_exists_deep($path, $arr)) {\n    // Do something with $arr['f']['b']['g']\n}