function foo($param_1) {\n    // Static variable to track whether the expensive operation has been done\n    static $called = false;\n\n    // Perform the expensive operation only on the first call\n    if (!$called) {\n        doExpensiveStuff($param_1);\n        $called = true;\n    }\n\n    echo '<b>' . htmlspecialchars($param_1) . '</b>';\n}\n\nfunction doExpensiveStuff($param) {\n    // Simulate an expensive operation, such as initializing resources\n    echo "Doing expensive stuff with {$param}...\n";\n}