function encryptString($string, $encryptionKey) {\n    // Generate an initialization vector (IV)\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    \n    // Encrypt the string\n    $encryptedData = openssl_encrypt($string, 'aes-256-cbc', hash('sha256', $encryptionKey), 0, $iv);\n    \n    // Return base64 encoded data and IV to store or transmit safely\n    return base64_encode($encryptedData . '::' . $iv);\n}\n\nfunction decryptString($encryptedString, $encryptionKey) {\n    // Split the encrypted string from its initialization vector (IV)\n    list($encryptedData, $iv) = explode('::', base64_decode($encryptedString));\n    \n    // Decrypt the data\n    return openssl_decrypt($encryptedData, 'aes-256-cbc', hash('sha256', $encryptionKey), 0, $iv);\n}\n\n// Example usage\n$key = "your-secret-key";\n$original = "Test";\n\n$encrypted = encryptString($original, $key);\necho "Encrypted: " . $encrypted . "\n";\n\n$decrypted = decryptString($encrypted, $key);\necho "Decrypted: " . $decrypted . "\n";