<?php\n\nfunction path_exists($array, $keys) {\n    foreach ($keys as $key) {\n        if (!isset($array[$key])) {\n            return false;\n        }\n        $array = $array[$key];\n    }\n    return true;\n}\n\n$array1 = [\n    "foo" => [\n        "bar" => [\n            "baz" => "value1"\n        ]\n    ]\n];\n\n$array2 = [\n    "foo" => [\n        "bar" => [\n            "baz" => "value2"\n        ]\n    ]\n];\n\n$path = ["foo", "bar", "baz"];\n\nif (path_exists($array1, $path) && path_exists($array2, $path)) {\n    echo "The path exists in both arrays.";\n} else {\n    echo "The path does not exist in both arrays.";\n}\n\n?>