$homePointsPlayerResult = mysql_query(\n    "SELECT `player_id`, COUNT(*) as conversion_count \n     FROM `conversions` \n     WHERE `game_id` = '$game_id' AND `team_id` IS NULL \n     GROUP BY `player_id'"\n);\n\nwhile ($row = mysql_fetch_assoc($homePointsPlayerResult)) {\n    $player_id = $row['player_id'];\n    if (array_search($player_id, array_column($home_players, 'player_id')) === FALSE) {\n        $home_players[] = [\n            'player_id' => $player_id,\n            'conversions' => $row['conversion_count']\n        ];\n    }\n}