// Start output buffering to capture both output and headers\nob_start("callback");\n\ninclude "script.php";  // This script might redirect using header("Location:");\n\n$out = ob_get_clean();  // Get everything that was buffered\n\n// Define the callback function to modify or suppress Location headers\nfunction callback($buffer) {\n    return $buffer; // Modify this if you need to alter the output\n}\n\n// List all headers that were sent in the buffer\n$headers = headers_list();\n\n// Clear all headers\nheader_remove();\n\n// Optionally, re-send only non-Location headers\nforeach ($headers as &$header) {\n    if (stripos($header, 'location') === false) {\n        header($header, true);\n    }\n}\n\necho $out;  // Output the buffered content