<?php\n$url = 'http://example.com/path/to/file';\n$headers = get_headers($url, 1);\n$gmtDate = $headers['Last-Modified'];\n\n// Create a DateTime object for the GMT date\n$dateGMT = new DateTime($gmtDate, new DateTimeZone('GMT'));\n\n// Set the timezone to Australia/Adelaide\n$dateAustraliaAdelaide = $dateGMT->setTimezone(new DateTimeZone('Australia/Adelaide'));\n\necho "Last Modified (Australia/Adelaide): " . $dateAustraliaAdelaide->format('d-M-Y H:i:s');\n?>