<?php\ndefined('BASEPATH') OR exit('No direct script access allowed');\n\n// Determine the environment\n$environment = '';\n\nif (gethostname() === 'production-server-hostname' || $_SERVER['ENVIRONMENT'] === 'production') {\n    $environment = '_production';\n} else {\n    $environment = '_development';\n}\n\n// Load environment-specific config file\n$config_file_name = 'config' . $environment;\nrequire_once(APPPATH . 'config/' . $config_file_name . '.php');\n\n// Merge environment-specific configurations with my_config.php if necessary\nforeach ($config as $key => $value) {\n    $config[$key] = isset($custom_config[$key]) ? $custom_config[$key] : $value;\n}