// For example, you can place this logic in a helper file and autoload it.\n\nfunction load_environment_config() {\n    $CI =& get_instance();\n    \n    // Determine the environment\n    if (gethostname() === 'production-server-hostname' || $_SERVER['ENVIRONMENT'] === 'production') {\n        $CI->config->load('config_production', TRUE);\n        \n        // Merge configurations\n        $prod_config = $CI->config->item('config_production');\n        foreach ($prod_config as $key => $value) {\n            $CI->config->set_item($key, $value);\n        }\n    } else {\n        $CI->config->load('config_development', TRUE);\n\n        // Merge configurations\n        $dev_config = $CI->config->item('config_development');\n        foreach ($dev_config as $key => $value) {\n            $CI->config->set_item($key, $value);\n        }\n    }\n}\n\n// Call this function from your main controller's constructor or a common preController hook.