<?php\n$filename = 'CSV Address.csv';\n$fileHandle = fopen($filename, 'r');\n\nif ($fileHandle !== FALSE) {\n    while (($data = fgetcsv($fileHandle, 0, ',', '"')) !== FALSE) {\n        // Assuming the CSV file has three fields: Name, Address, Status\n        $name[] = $data[0];\n        $address[] = $data[1];\n        $status[] = $data[2];\n    }\n    fclose($fileHandle);\n}\n\n// Output data to check if it's read correctly\nprint_r($name);\nprint_r($address);\nprint_r($status);\n?>