$array_A = array(13, 4, 59, 38, 9, 69, 72, 93, 1, 3, 5);\n$array_B = array(29, 72, 21, 3, 6);\n\n// Convert one of the arrays to an associative array for fast lookup\n$map_A = array_flip($array_A);\n\n$count_common = 0;\n\n// Iterate through the second array and check if each element exists in the map\nforeach ($array_B as $value) {\n    if (isset($map_A[$value])) {\n        $count_common++;\n        // Optionally, you could unset to handle duplicates only once\n        // unset($map_A[$value]);\n    }\n}\n\necho "Number of common integers: " . $count_common;