$dom = new DOMDocument();\n$dom->loadHTML('<html><body><div class="example">Content</div></body></html>');\n\n// Get the elements by tag name\n$nodes = $dom->getElementsByTagName('div');\n\nforeach ($nodes as $node) {\n    // Check if the node is a DOMElement\n    if ($node instanceof DOMElement) {\n        // Now you can use DOMElement specific methods\n        echo $node->tagName; // Outputs: div\n        echo "\n";\n        // Example of using getElementsByTagName on this element\n        $children = $node->getElementsByTagName('*');\n        foreach ($children as $child) {\n            echo $child->nodeName . " found inside div.\n";\n        }\n    }\n}