class AclassName {\n    public function doSomething() {\n        echo "Doing something!";\n    }\n}\n\nclass AnotherClass {\n    private $foo;\n\n    public function __construct(AclassName $foo) {\n        $this->foo = $foo;\n    }\n\n    public function useDependency() {\n        $this->foo->doSomething();\n    }\n}\n\n// Usage\n$dependency = new AclassName();\n$instance = new AnotherClass($dependency);\n$instance->useDependency(); // Outputs: Doing something!