<?php\n// Connect to the inbox mail server\n$mailbox = '{imap.gmail.com:993/imap/ssl}INBOX';\n$username = 'bounce_handle@domain.com';\n$password = 'yourpassword';\n\n// Open the mail connection\n$connection = imap_open($mailbox, $username, $password);\n\nif (!$connection) {\n    die('Cannot connect to mailbox');\n}\n\n// Fetch emails from the inbox\n$emails = imap_search($connection, 'ALL');\n\nif ($emails) {\n    // Sort emails from newest to oldest\n    rsort($emails);\n\n    foreach ($emails as $email_number) {\n        // Get header information\n        $overview = imap_fetch_overview($connection, $email_number, 0);\n        $message = imap_body($connection, $email_number);\n\n        if (strpos($overview[0]->subject, 'Mail Delivery System') !== false || strpos($message, 'Delivery failure') !== false) {\n            // Process bounce email\n            echo "Bounced Email Found: " . $overview[0]->subject . "\n";\n            echo "From: " . $overview[0]->from . "\n";\n            echo "Body: " . $message . "\n";\n\n            // Handle the bounced email, e.g., log it or remove invalid addresses from your list\n        }\n\n        // You might want to delete processed bounces if necessary\n        imap_delete($connection, $email_number);\n    }\n} else {\n    echo 'No new emails';\n}\n\n// Close the mailbox connection\nimap_expunge($connection);\nimap_close($connection);\n?>