<?php\n// URL of the remote video file\n$remoteFileUrl = 'http://example.com/videofolder/video.mp4';\n\n// Initialize session\n$session = curl_init($remoteFileUrl);\n\nif ($session) {\n    // Set cURL options\n    curl_setopt($session, CURLOPT_RETURNTRANSFER, 1);\n    \n    // Execute cURL request\n    $fileData = curl_exec($session);\n    \n    // Close cURL session\n    curl_close($session);\n\n    if ($fileData !== false) {\n        // Filename to show in the browser's download dialog\n        $fileName = basename($remoteFileUrl);\n\n        // Set headers\n        header('Content-Type: application/octet-stream');\n        header('Content-Disposition: attachment; filename="' . $fileName . '"');\n\n        // Output data\n        echo $fileData;\n    } else {\n        http_response_code(500);\n        die('Error downloading file.');\n    }\n} else {\n    http_response_code(404);\n    die('File not found.');\n}\n?>