function truncateString($string, $limit) {\n    if (strlen($string) <= $limit) return $string;\n\n    // Find the last space within the limit\n    $lastSpace = strpos(substr($string, 0, $limit), ' ');\n\n    // If no space is found or we are at the beginning, the entire substring up to limit is a word\n    if ($lastSpace === false || $lastSpace === 0) {\n        return substr($string, 0, $limit);\n    }\n\n    // Truncate the string after the last space found within the limit\n    return rtrim(substr($string, 0, strrpos(substr($string, 0, $limit), ' '))) . '...';\n}\n\n$textFromDatabase = "Your long text from the database goes here...";\n$truncatedText = truncateString($textFromDatabase, 200);\necho $truncatedText;