<?php\n// Check if 'file' parameter is set in the URL\nif (isset($_GET['file'])) {\n    $filePath = $_GET['file']; // This should be sanitized to prevent directory traversal attacks\n\n    // Sanitize and validate the file path\n    $allowedDirectory = __DIR__ . '/uploads/'; // Define the directory where files are stored\n    if (strpos($filePath, '..') === false && is_file($allowedDirectory . basename($filePath))) {\n        $filePath = $allowedDirectory . basename($filePath);\n        \n        // Set headers to force download\n        header('Content-Description: File Transfer');\n        header('Content-Type: application/octet-stream');\n        header('Content-Disposition: attachment; filename="' . basename($filePath) . '"');\n        header('Expires: 0');\n        header('Cache-Control: must-revalidate');\n        header('Pragma: public');\n        header('Content-Length: ' . filesize($filePath));\n        readfile($filePath);\n        exit;\n    } else {\n        die("File not found.");\n    }\n}\n?>