<?php\n\n$sourceDir = '/path/to/source/directory'; // Directory to compress\n$outputFile = '/path/to/output/archive.tar.gz'; // Output compressed file path\n\ntry {\n    $phar = new PharData($outputFile);\n    $defaultStub = $phar->createDefault Stub('index.php', '/path/to/index.php'); // Optional: for phars with executables\n    $phar->buildFromDirectory($sourceDir); // Add files from the source directory\n    $phar->compressFiles(Phar::GZ); // Compress using GZIP\n\n    echo "The directory {$sourceDir} has been compressed into {$outputFile}\n";\n} catch (Exception $e) {\n    die('Creating tarball failed because: ' . $e);\n}\n\n?>