class Profile {\n    const LABEL_FIRST_NAME = "First Name";\n    const LABEL_LAST_NAME = "Last Name";\n    const LABEL_COMPANY_NAME = "Company";\n}\n\n$reflectionClass = new \ReflectionClass('Profile');\n$constants = $reflectionClass->getConstants();\n\n// To get just the names of the constants:\n$constantNames = array_keys($constants);\nprint_r($constantNames);\n\n// To get the full name with class scope:\n$fullConstantNames = array_map(function($name) {\n    return 'Profile::' . $name;\n}, $constantNames);\nprint_r($fullConstantNames);\n\n// To get an associative array of names and values:\n$constantsWithValues = $reflectionClass->getConstants();\nprint_r($constantsWithValues);