// Sample data arrays\n$data1 = [['A' => 'A0', 'B' => 'B0'], ['A' => 'A1', 'B' => 'B1']];\n$data2 = [['A' => 'A2', 'C' => 'C2'], ['A' => 'A3', 'C' => 'C3']];\n\n// Simple function to join arrays on a key\nfunction array_join($arr1, $arr2, $key) {\n    $result = [];\n    foreach ($arr1 as $a1) {\n        foreach ($arr2 as $a2) {\n            if ($a1[$key] === $a2[$key]) {\n                $result[] = array_merge($a1, $a2);\n            }\n        }\n    }\n    return $result;\n}\n\n$result = array_join($data1, $data2, 'A');\nprint_r($result);