$result = "Country: USA City: NY Latitude: 32.2667 Longitude: 71.9167";\n\n// Use preg_match_all to extract key-value pairs\npreg_match_all('/(\w+):\s*(\S+)/', $result, $matches);\n\n// Initialize an array to hold the formatted output\n$pairs = [];\n\n// Loop through matches and construct key=value strings\nfor ($i = 0; $i < count($matches[1]); $i++) {\n    $pairs[] = $matches[1][$i] . '=' . $matches[2][$i];\n}\n\n// Join all pairs with '&' to form the final output string\n$output = implode('&', $pairs);\n\necho $output;