function timeDiff($firstTime, $lastTime) {\n    // Split the time strings into minutes and seconds\n    list($firstMinutes, $firstSeconds) = explode(':', $firstTime);\n    list($lastMinutes, $lastSeconds) = explode(':', $lastTime);\n\n    // Convert everything to seconds\n    $firstTotalSeconds = ($firstMinutes * 60) + (int)$firstSeconds;\n    $lastTotalSeconds = ($lastMinutes * 60) + (int)$lastSeconds;\n\n    // Calculate the difference in seconds\n    $differenceInSeconds = $lastTotalSeconds - $firstTotalSeconds;\n\n    // Convert back to minutes and seconds format\n    $diffMinutes = floor($differenceInSeconds / 60);\n    $diffSeconds = $differenceInSeconds % 60;\n\n    // Format the result as "minutes:seconds"\n    return sprintf("%d:%02d", $diffMinutes, $diffSeconds);\n}\n\n// Example usage\necho timeDiff('1:07', '3:01'); // Outputs: 1:54