// Assuming $db is an instance of Zend_Db_Adapter_Abstract\n// get open/closed\n$sql = 'SELECT status, count(*) as total \n        FROM reported_issues \n        WHERE date_reported >= ? AND status IN (0,1) \n        GROUP BY status';\n$stmt = $db->query($sql, [$today]);\n\nwhile ($row = $stmt->fetch()) {\n    switch ($row['status']) {\n        case 0:\n            $totalIssuesToday = $row['total'];\n            break;\n\n        case 1:\n            $totalIssuesClosedToday = $row['total'];\n            break;\n    }\n}