libxml_use_internal_errors(true); // Disable libxml errors\n\n$dom = new DOMDocument();\nif ($dom->load($tempFileName, LIBXML_DTDVALID | LIBXML_NOENT)) {\n    if ($dom->validate()) {\n        echo '<p>XML is valid. Overwriting sitemap.xml.</p>';\n        file_put_contents('sitemap.xml', $dom->saveXML());\n    } else {\n        echo '<p>XML is not valid. Please correct.</p>';\n        $errors = libxml_get_errors();\n        foreach ($errors as $error) {\n            echo "Validation error: {$error->message}\n";\n        }\n        libxml_clear_errors(); // Clear any remaining errors\n    }\n} else {\n    echo '<p>Error loading XML file. Please correct.</p>';\n    $errors = libxml_get_errors();\n    foreach ($errors as $error) {\n        echo "Loading error: {$error->message}\n";\n    }\n    libxml_clear_errors(); // Clear any remaining errors\n}