<?php\n\n$num_years = count($data);\n$which_year = 1; // The index of the sub-array to sort by, e.g., 1 for the second year.\n\n// Create an array with references to all sub-arrays in $data.\n$args = [];\nfor ($i = 0; $i < $num_years; $i++) {\n    $args[] = &$data[$i];\n}\n\n// Insert the sorting parameters at the beginning of the argument list: \n// The reference to the year we are sorting by and the sort flag.\narray_unshift($args, &$data[$which_year], SORT_ASC);\n\n// Call array_multisort with a dynamic number of arguments using ...\narray_multisort(...$args);\n\nprint_r($data);\n?>