function massMail($recipients, $subject, $content) {\n    $headers = "From: noreply@yourblog.com\r\n";\n    $headers .= "Content-Type: text/html; charset=UTF-8\r\n";\n    \n    // Set a limit on the number of emails to send per minute\n    $emailsPerMinute = 10;\n    $pauseTime = 60 / $emailsPerMinute;\n    \n    foreach ($recipients as $index => $r) {\n        $_content = $content . '<img src="http://xxx/trackOpenRate.php?id=' . urlencode($r) . '">';\n        \n        if (mail($r, $subject, $_content, $headers)) {\n            echo "Email sent to: $r\n";\n        } else {\n            echo "Failed to send email to: $r\n";\n        }\n        \n        // Pause between sending emails\n        sleep($pauseTime);\n    }\n}