$sql = 'SELECT photographers.photographer_id, photographers.photographer_name FROM photographers';\n\n$stmt = $conn->stmt_init();\n$resultArray = []; // Initialize an empty array to store results\n\nif ($stmt->prepare($sql)) {\n    $stmt->execute();\n\n    // Use a result class or bound variables. Here we use bound vars.\n    $stmt->bind_result($photographer_id, $photographer_name);\n\n    // Fetch values\n    while ($stmt->fetch()) {\n        // Create an associative array for each row and push it onto the results array\n        $resultArray[] = [\n            'photographer_id' => $photographer_id,\n            'photographer_name' => $photographer_name\n        ];\n    }\n\n    // Now you can loop through $resultArray to access all the data\n}\n\n$stmt->close();