function checkOracleExtensions() {\n    $oci8Loaded = extension_loaded('oci8');\n    $pdoOciLoaded = extension_loaded('pdo_oci');\n\n    return [\n        'oci8' => $oci8Loaded,\n        'pdo_oci' => $pdoOciLoaded\n    ];\n}\n\n$extensions = checkOracleExtensions();\nif (!$extensions['oci8'] && !$extensions['pdo_oci']) {\n    echo "Neither oci8 nor pdo_oci are installed.";\n} else {\n    echo "Installed Oracle extensions: ";\n    if ($extensions['oci8']) echo "oci8 ";\n    if ($extensions['pdo_oci']) echo "pdo_oci";\n}