function add_custom_rewrite_rules() {\n    // Redirects /discusses/category_name/ to the appropriate archive page.\n    add_rewrite_rule('^discusses/([^/]*)/?','index.php?post_type=articles&category_name=$matches[1]','top');\n\n    // Redirects /does/category_name/ to the appropriate archive page.\n    add_rewrite_rule('^does/([^/]*)/?','index.php?post_type=portfolio&category_name=$matches[1]','top');\n}\nadd_action('init', 'add_custom_rewrite_rules');