function detect_markup($text) {\n    // Markdown has specific patterns like `#` for headers or `[link text](url)`\n    $markdown_pattern = '/(^|\n)\s*#{1,6} | \[.*?\]\(.*?\)/';\n    // Textile uses different conventions like `h1. Title` or `"Link":http://example.com`\n    $textile_pattern = '/(^|\n)\s*h\d\.\s| ".*?":\S+/';\n\n    if (preg_match($markdown_pattern, $text)) {\n        return 'Markdown';\n    } else if (preg_match($textile_pattern, $text)) {\n        return 'Textile';\n    }\n\n    // Fallback, might want to improve this\n    return 'Unknown or mixed format';\n}\n\necho detect_markup("# Header\n");  // Output: Markdown\necho detect_markup("h1. Header\n");  // Output: Textile