<?php\nfunction getSVNRevision($path = '.') {\n    // Execute the svn info command and capture the output\n    $output = [];\n    exec("svn info --show-item revision " . escapeshellarg(realpath($path)), $output, $returnVar);\n\n    // Check if the command was successful\n    if ($returnVar !== 0) {\n        return 'Error retrieving SVN revision';\n    }\n\n    // Return the revision number as a string\n    return isset($output[0]) ? trim($output[0]) : 'Unknown revision';\n}\n\necho "Current SVN Revision: " . getSVNRevision() . "\n";\n?>