class MyClass {\n    private $changeSort;\n\n    // Magic setter method\n    public function __set($name, $value) {\n        if (property_exists($this, $name)) {\n            $this->$name = $value;\n        }\n    }\n\n    // Magic getter method\n    public function __get($name) {\n        if (property_exists($this, $name)) {\n            return $this->$name;\n        }\n    }\n}\n\n// Usage\n$obj = new MyClass();\n$obj->changeSort = 'someValue';  // Automatically calls __set\necho $obj->changeSort;          // Automatically calls __get