<?php\nrequire_once 'Zend/Loader/Autoloader.php';\nZend_Loader_Autoloader::getInstance();\n\n$user = 'name@example.com';\n$pass = 'password'; // Avoid hardcoding passwords in production\n$service = Zend_Gdata_Blogger::AUTH_SERVICE_NAME;\n\n$client = Zend_Gdata_ClientLogin::getHttpClient($user, $pass, $service);\n$blogger = new Zend_Gdata_Blogger($client);\n\nfunction createPublishedPost($blogger, $title='Hello, world!', $content='I am blogging on the internet.')\n{\n    $blogID = '7973737751295446679';\n    $uri = 'http://www.blogger.com/feeds/' . $blogID . '/posts/default';\n\n    $entry = $blogger->newPostEntry();\n    $entry->title = $blogger->newTitle($title);\n    $entry->content = $blogger->newContent($content);\n    $entry->content->setType('text/plain');\n\n    try {\n        $createdPost = $blogger->insertGadgetFeed($entry, $uri);\n        return $createdPost;\n    } catch (Zend_Gdata_App_Exception $e) {\n        echo "Error: " . $e->getMessage();\n    }\n}\n\n$post = createPublishedPost($blogger);\nif ($post) {\n    echo "Post created successfully with ID: " . $post->id;\n}\n?>