// Load the Twilio PHP library from twilio.com/docs/php/install\nrequire_once '/path/to/twilio-php/src/Twilio/autoload.php';\n\nuse Twilio\Rest\Client;\n\n$sid    = "your_account_sid"; // Your Account SID from www.twilio.com/console\n$token  = "your_auth_token";  // Your Auth Token from www.twilio.com/console\n$client = new Client($sid, $token);\n\n$message = $client->messages->create(\n    "+1234567890", // Text this number\n    [\n        'from' => "+0987654321", // From a valid Twilio number\n        'body' => "Hello from PHP!"\n    ]\n);\n\necho $message->sid;