<?php\n// Assuming the ID is passed via GET request\n$id = filter_input(INPUT_GET, 'id', FILTER_SANITIZE_NUMBER_INT);\n\n// Determine which server to fetch from based on your conditions\n$file_url = ($some_condition) ? "http://servera.com/$id.exe" : "http://serverb.com/$id.exe";\n\n// Initialize cURL session\n$ch = curl_init($file_url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the request and fetch the file contents\n$file_contents = curl_exec($ch);\n\nif ($file_contents === false) {\n    // Handle error if the file could not be fetched\n    http_response_code(500);\n    die('Failed to download file.');\n}\n\n// Get the content type of the file from the HTTP headers (optional but recommended)\n$contentType = curl_getinfo($ch, CURLINFO_CONTENT_TYPE);\n\n// Close the cURL session\ncurl_close($ch);\n\n// Send proper headers to inform the browser that a file is coming down\nheader('Content-Type: ' . ($contentType ?: 'application/octet-stream'));\nheader("Content-Disposition: attachment; filename=$id.exe");\nheader('Expires: 0');\nheader('Cache-Control: must-revalidate, post-check=0, pre-check=0');\n\n// Output the file contents to the browser\necho $file_contents;\n?>