<?php\nrequire_once "xajax/xajax_core/xajax.inc.php";\n$xajax = new xajax();\n$xajax->register(XAJAX_FUNCTION, 'loadMessages');\n\nfunction loadMessages() {\n    // Simulate fetching new chat messages from the database or a file\n    $messages[] = date("Y-m-d H:i:s") . " - New message received";\n    $response = new xajaxResponse();\n    foreach ($messages as $msg) {\n        $response->addAppend('chatOutput', 'innerHTML', "<p>$msg</p>");\n    }\n    return $response;\n}\n\n$xajax->processRequest();\n?>