function formatVideoDuration($time) {\n    // Split the time string into components\n    list($hours, $minutes, $seconds) = explode(':', $time);\n    \n    // Start building the output string\n    $output = '';\n    \n    // Add hours to the output if greater than 0\n    if ($hours > 0) {\n        $output .= $hours . " hour" . (($hours != 1) ? "s" : "") . ", ";\n    }\n    \n    // Add minutes to the output\n    $output .= $minutes . " minute" . (($minutes != 1) ? "s" : "");\n    \n    // Only add seconds if hours are not present or minutes are exactly 0\n    if ($hours == 0 || $minutes == 0) {\n        $output .= ", ";\n    }\n    \n    // Add seconds to the output\n    $output .= $seconds . " second" . (($seconds != 1) ? "s" : "");\n    \n    return $output;\n}\n\n// Example usage:\n$duration = "02:34:56";\necho formatVideoDuration($duration); // Outputs: "2 hours, 34 minutes, 56 seconds"\n\n$shortDuration = "00:01:23";\necho formatVideoDuration($shortDuration); // Outputs: "1 minute, 23 seconds"