<?php\nif (isset($_SERVER['HTTP_ACCEPT_LANGUAGE'])) {\n    $lang = substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 0, 2); // Get the first two characters\n\n    if ($lang === 'en') {\n        // Further check for specific region if needed\n        if (strpos(strtolower($_SERVER['HTTP_ACCEPT_LANGUAGE']), 'us') !== false) {\n            include('http://page.com/us.txt');\n        } elseif (strpos(strtolower($_SERVER['HTTP_ACCEPT_LANGUAGE']), 'uk') !== false) {\n            include('http://page.com/uk.txt');\n        } else {\n            // Default language handling if it's not US or UK\n            include('http://page.com/default.txt');\n        }\n    } else {\n        // Handle other languages if needed\n        include('http://page.com/default.txt');\n    }\n} else {\n    // Fallback if no language is set\n    include('http://page.com/default.txt');\n}\n?>