<?php\n$servername = "localhost";\n$username = "your_username";\n$password = "your_password";\n$dbname = "bookfeather";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Initialize the SQL query\n$sql = '';\n\n// List of tables (assuming they are named table1, table2, ..., table56)\nfor ($i = 1; $i <= 56; $i++) {\n    if (!empty($sql)) {\n        $sql .= " UNION ALL ";\n    }\n    $sql .= "SELECT site, votes_up FROM table$i";\n}\n\n// Group and order the results\n$sql .= " GROUP BY site ORDER BY SUM(votes_up) DESC LIMIT 25";\n\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        echo "Site: " . htmlspecialchars($row["site"]) . " - Total Votes Up: " . htmlspecialchars($row["SUM(votes_up)"]) . "<br>";\n    }\n} else {\n    echo "0 results";\n}\n$conn->close();\n?>