<?php\n\nrequire __DIR__ . '/vendor/autoload.php';\n\nuse Symfony\Component\Form\Forms;\nuse Symfony\Component\Form\Extension\HttpFoundation\HttpFoundationExtension;\nuse Symfony\Component\HttpFoundation\Request;\nuse Symfony\Bridge\Twig\Extension\FormExtension;\nuse Twig\Environment;\nuse Twig\Loader\FilesystemLoader;\n\n// Setup the Form component with the CSRF protection and HTTP foundation extensions\n$formFactory = Forms::createFormFactoryBuilder()\n    ->getFormFactory();\n\n$twig = new Environment(new FilesystemLoader(__DIR__ . '/templates'));\n\n$request = Request::createFromGlobals();\n$form = $formFactory->create(ContactFormType::class);\n$form->handleRequest($request);\n\nif ($form->isSubmitted() && $form->isValid()) {\n    // Data is an array with "name", "email", and "submit" fields\n    $data = $form->getData();\n\n    echo "Name: ". htmlspecialchars($data['name']);\n    echo "<br>Email: ". htmlspecialchars($data['email']);\n\n    return;\n}\n\necho $twig->render('contact_form.html.twig', ['form' => $form->createView()]);