// In your OAuth server or login system after successful authentication\nuse Firebase\JWT\JWT;\n\n$key = 'secret_key_here'; // This should be kept secure and shared between applications\n$payload = array(\n    "iss" => "your_domain.com", // Issuer of the token\n    "sub" => "1234567890",      // Subject (user id)\n    "iat" => time(),            // Issued at\n    "exp" => time() + 3600,     // Expiration time (one hour)\n);\n\n$jwt = JWT::encode($payload, $key);