ini_set('session.gc_maxlifetime', 900); // Set max lifetime, but not activity timeout\n\nsession_start();\n\n// Check if 'last_time' has been set; create new one if not\nif (!isset($_SESSION['last_time'])) {\n    $_SESSION['last_time'] = time();\n}\n\n// Calculate inactivity period and compare to 15 minutes (900 seconds)\n$inactivity_period = 900;\nif ((time() - $_SESSION['last_time']) > $inactivity_period) {\n    session_unset();\n    session_destroy();\n} else {\n    // Update last activity time\n    $_SESSION['last_time'] = time();\n}