$to = 'recipient@example.com';\n$subject = 'Test Email with Delivery and Read Receipts';\n$message = 'This is a test email.';\n\n// Set headers for delivery report (return receipt) and read receipt\n$headers = "Disposition-Notification-To: sender@example.com\r\n"; // Request return receipt\n$headers .= "Return-Receipt-To: sender@example.com\r\n";          // Another way to request return receipt\n$headers .= "X-Confirm-Reading-To: sender@example.com\r\n";      // Additional header for some clients\n\n// Send the email\nmail($to, $subject, $message, $headers);