$dateString = "Jan 15 2005 12:00AM";\n$format = "M d Y H:iA"; // Define the format of your date string\n\n// Create a DateTime object from the string using the defined format\n$dateTimeObject = DateTime::createFromFormat($format, $dateString);\n\nif ($dateTimeObject) {\n    // Get the Unix timestamp\n    $timestamp = $dateTimeObject->getTimestamp();\n    echo "The timestamp is: " . $timestamp;\n} else {\n    echo "Failed to parse date.";\n}